(defn parse-longs [s]
  (->> (re-seq #"\d+" s)
       (map parse-long)))

(defn parse-input [input]
  (set (->> (str/split-lines input)
            (map parse-longs)
            (map vec))))

(defn neighbours [p]
  (let [[x y z] p]
    (set (for [dx [-1 0 1]
               dy [-1 0 1]
               dz [-1 0 1]
               :when (= 1 (+ (abs dx) (abs dy) (abs dz)))]
           [(+ x dx) (+ y dy) (+ z dz)]))))

(defn part1 []
  (let [cubes (parse-input input)]
    (->> (map #(set/difference (neighbours %) cubes) cubes)
         (map count)
         (reduce +))))

(let [cubes (parse-input input)
      min-x (dec (reduce min (map #(nth % 0) cubes)))
      max-x (inc (reduce max (map #(nth % 0) cubes)))
      min-y (dec (reduce min (map #(nth % 1) cubes)))
      max-y (inc (reduce max (map #(nth % 1) cubes)))
      min-z (dec (reduce min (map #(nth % 2) cubes)))
      max-z (inc (reduce max (map #(nth % 2) cubes)))
      inside-bbox? (fn [[x y z]]
                     (and (<= min-x x max-x)
                          (<= min-y y max-y)
                          (<= min-z z max-z)))
      filled (loop [fill [[min-x min-y min-z]], cubes cubes]
               (if (empty? fill)
                 cubes
                 (recur (->> (set/difference (neighbours (peek fill)) cubes)
                             (filter inside-bbox?)
                             (into (pop fill)))
                        (conj cubes (peek fill)))))]
  (- (part1)
     (->> (map #(set/difference (neighbours %) filled) cubes)
          (map count)
          (reduce +))))