(defn parse-play1 [s]
  (->> (str/split s #" ")
       (map {"A" :rock "B" :paper "C" :scissors
             "X" :rock "Y" :paper "Z" :scissors})))

(defn parse-input1 [input]
  (->> (str/split input #"\n")
       (map parse-play1)))

(def hand-score
  {:rock     1
   :paper    2
   :scissors 3})

(def outcome-score
  {[:rock :rock]         3
   [:rock :paper]        6
   [:rock :scissors]     0
   [:paper :rock]        0
   [:paper :paper]       3
   [:paper :scissors]    6
   [:scissors :rock]     6
   [:scissors :paper]    0
   [:scissors :scissors] 3})

(defn score [play]
  (+ (hand-score (second play)) (outcome-score play)))

(defn part1 []
  (->>
    (parse-input1 input)
    (map score)
    (reduce +)))

(defn parse-guide [s]
  (->> (str/split s #" ")
       (map {"A" :rock "B" :paper "C" :scissors
             "X" :lose "Y" :draw "Z" :win})))

(defn parse-input2 [input]
  (->> (str/split input #"\n")
       (map parse-guide)))

(def guide->play
  {[:rock :lose]     [:rock :scissors]
   [:rock :draw]     [:rock :rock]
   [:rock :win]      [:rock :paper]
   [:paper :lose]    [:paper :rock]
   [:paper :draw]    [:paper :paper]
   [:paper :win]     [:paper :scissors]
   [:scissors :lose] [:scissors :paper]
   [:scissors :draw] [:scissors :scissors]
   [:scissors :win]  [:scissors :rock]})

(defn part2 []
  (->>
    (parse-input2 input)
    (map guide->play)
    (map score)
    (reduce +)))
