(ns aoc.day6)

(def input "")

(defn find-start [input len]
  (->> (partition len 1 input)
       (map set)
       (map-indexed (fn [idx chars] [(+ idx len) chars]))
       (filter (fn [[_ chars]] (= (count chars) len)))
       (first)
       (first)))

(defn part1 []
  (find-start input 4))

(defn part2 []
  (find-start input 14))